pragma solidity ^0.5.0;
import "./PikoToken.sol";

contract PikoTokenSale {
  address payable admin;
  PikoToken public tokenContract;
  uint256 public tokenPrice;
  uint256 public tokensSold;

  event Sell(address _buyer, uint256 _amount);

  constructor(PikoToken _tokenContract, uint256 _tokenPrice) public{
    admin = msg.sender;
    tokenContract = _tokenContract;
    tokenPrice = _tokenPrice;
  }

  function buyTokens(uint256 _numberOfTokens) public payable {
    require(msg.value == multiply(_numberOfTokens, tokenPrice), "the payable value should equal the cost (tokens * token price)");
    require(tokenContract.balanceOf(address(this)) >= _numberOfTokens, "Smart contract does not have enough tokens");
    require(tokenContract.transfer(msg.sender, _numberOfTokens), "Failed to transfer tokens");
    tokensSold += _numberOfTokens;
    emit Sell(msg.sender, _numberOfTokens);
  }

  //based on ds-math multiply
  function multiply(uint x, uint y) private pure returns (uint z){
    require(y == 0 || (z = x*y ) / y == x, "Invalid multiplier in multiply()");
  }

  function endSale() public {
    require(msg.sender == admin, "must be admin account to end sale");
    require(tokenContract.transfer(admin, tokenContract.balanceOf(address(this))), "transfering tokens to admin account at end of sale");
    //selfdestruct(admin);
  }
}
